import { useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Header from "./components/Header.tsx";
import Footer from "./components/Footer.tsx";
import Navbar from "./components/Navbar.tsx";
import LoginForm from "./components/LoginForm.tsx";
import React from "react";
import ApplicantList from "./components/ApplicantList.tsx";

const Home = () => (
  <div className="p-8 max-w-3xl mx-auto text-center">
    {/* 用户头像 */}
    <img
      src="https://uifaces.co/our-content/donated/1H_7AxP0.jpg"
      alt="Welcome"
      className="mx-auto w-32 h-32 rounded-full shadow mb-6"
    />

    {/* 欢迎标题 */}
    <h1 className="text-4xl font-bold mb-4 text-gray-800">
      欢迎来到 <span className="text-blue-600">TeachTeam</span> 系统
    </h1>

    {/* 系统简介 */}
    <p className="text-lg text-gray-700 mb-6 leading-relaxed">
      TeachTeam 是计算机学院用于招聘兼职导师的内部系统。
      教师可以筛选并评价申请人，申请人也可以填写详细的导师申请资料。
    </p>

    {/* 操作建议按钮（仅展示样式用，无功能） */}
    <div className="flex justify-center gap-4 mt-6">
      <a
        href="/login"
        className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded shadow"
      >
        立即登录
      </a>
      <a
        href="/signup"
        className="bg-gray-200 hover:bg-gray-300 text-gray-800 px-5 py-2 rounded shadow"
      >
        注册申请人
      </a>
    </div>
  </div>
);


function App() {
  useEffect(() => {
    const existingUser = localStorage.getItem("user");
    if (!existingUser) {
      localStorage.setItem(
        "user",
        JSON.stringify({
          email: "test@rmit.edu.au",
          password: "StrongPass123",
        })
      );
    }
  }, []);

  return (
    <Router>
      <div className="flex flex-col min-h-screen">
        <Header />
        <Navbar />
        <main className="flex-grow">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<LoginForm />} />
            <Route path="/lecturer" element={<ApplicantList />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
