import React, { useEffect, useState } from "react";

interface Applicant {
  name: string;
  course: string;
  availability: string;
  skills: string;
  credentials: string;
}

interface Selected {
  name: string;
  comment: string;
  rank: number;
}

const LecturerPanel = () => {
  const [applicants, setApplicants] = useState<Applicant[]>([]);
  const [selected, setSelected] = useState<Selected[]>([]);

  useEffect(() => {
    const stored = localStorage.getItem("tutorApplication");
    if (stored) {
      const data = JSON.parse(stored);
      const formatted: Applicant = {
        name: "Test User",
        course: data.selectedCourses.join(", "),
        availability: data.availability,
        skills: data.skills,
        credentials: data.credentials,
      };
      setApplicants([formatted]);
    }
  }, []);

  const toggleSelect = (name: string) => {
    if (selected.find((s) => s.name === name)) {
      setSelected((prev) => prev.filter((s) => s.name !== name));
    } else {
      setSelected((prev) => [...prev, { name, comment: "", rank: prev.length + 1 }]);
    }
  };

  const updateComment = (name: string, comment: string) => {
    setSelected((prev) => prev.map((s) => (s.name === name ? { ...s, comment } : s)));
  };

  const updateRank = (name: string, rank: number) => {
    setSelected((prev) => prev.map((s) => (s.name === name ? { ...s, rank } : s)));
  };

  return (
    <div className="p-6 max-w-5xl mx-auto mt-6">
      <h2 className="text-2xl font-bold mb-4 text-center">Lecturer Review Panel</h2>
      {applicants.length === 0 ? (
        <p className="text-center text-gray-600">No applicant data found.</p>
      ) : (
        <ul className="space-y-4">
          {applicants.map((app, idx) => {
            const isSelected = selected.some((s) => s.name === app.name);
            const selData = selected.find((s) => s.name === app.name);
            return (
              <li key={idx} className={`border p-4 rounded shadow ${isSelected ? "bg-green-50 border-green-500" : ""}`}>
                <div className="flex justify-between items-center">
                  <p className="font-semibold text-lg">{app.name} {isSelected && "✅"}</p>
                  {isSelected && <span className="bg-blue-600 text-white px-2 py-1 rounded text-sm">Rank: {selData?.rank}</span>}
                </div>
                <p><strong>Course:</strong> {app.course}</p>
                <p><strong>Availability:</strong> <span className="px-2 py-1 rounded text-white bg-yellow-500 text-xs">{app.availability}</span></p>
                <p><strong>Skills:</strong> {app.skills.split(",").map((s, i) => (
                  <span key={i} className="bg-gray-200 text-xs px-2 py-1 rounded mr-1">{s.trim()}</span>
                ))}</p>
                <p><strong>Credentials:</strong> {app.credentials}</p>

                <div className="mt-2">
                  <label><input type="checkbox" checked={isSelected} onChange={() => toggleSelect(app.name)} /> Select</label>
                </div>

                {isSelected && (
                  <div className="mt-2 space-y-2">
                    <textarea className="w-full border p-2 rounded" placeholder="Comment" value={selData?.comment || ""} onChange={(e) => updateComment(app.name, e.target.value)} />
                    <input type="number" min={1} max={5} className="border p-2 rounded w-20" value={selData?.rank || 1} onChange={(e) => updateRank(app.name, parseInt(e.target.value))} />
                  </div>
                )}
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
};

export default LecturerPanel;
