import React, { useEffect, useState } from "react";

// 定义申请人结构
interface Applicant {
  name: string;
  course: string;
  availability: string;
  skills: string;
  credentials: string;
}

// 教师选择结构
interface Selected {
  name: string;
  comment: string;
  rank: number;
}

// ✅ 自定义 Hook：标记 most / least / unchosen
const useSelectionStats = (applicants: Applicant[], selected: Selected[]) => {
  const counts = applicants.map(app => {
    const match = selected.find(s => s.name === app.name);
    return {
      ...app,
      selectedCount: match ? 1 : 0
    };
  });

  const max = Math.max(...counts.map(c => c.selectedCount));
  const min = Math.min(...counts.map(c => c.selectedCount));

  return counts.map(app => ({
    ...app,
    isMostChosen: app.selectedCount === max && max !== 0,
    isLeastChosen: app.selectedCount === min && min !== max,
    isUnchosen: app.selectedCount === 0
  }));
};

const LecturerPanel = () => {
  const [applicants, setApplicants] = useState<Applicant[]>([]);
  const [selected, setSelected] = useState<Selected[]>([]);

  // 页面加载时读取申请人和评审数据
  useEffect(() => {
    const stored = localStorage.getItem("tutorApplication");
    if (stored) {
      const data = JSON.parse(stored);
      const formatted: Applicant = {
        name: "Test User",
        course: data.selectedCourses.join(", "),
        availability: data.availability,
        skills: data.skills,
        credentials: data.credentials
      };
      setApplicants([formatted]);
    }

    const storedReview = localStorage.getItem("selectedReviewData");
    if (storedReview) {
      setSelected(JSON.parse(storedReview));
    }
  }, []);

  // 每次更改选择内容，自动保存到 localStorage
  useEffect(() => {
    localStorage.setItem("selectedReviewData", JSON.stringify(selected));
  }, [selected]);

  // 选择候选人
  const toggleSelect = (name: string) => {
    if (selected.find(sel => sel.name === name)) {
      setSelected(prev => prev.filter(sel => sel.name !== name));
    } else {
      setSelected(prev => [...prev, { name, comment: "", rank: prev.length + 1 }]);
    }
  };

  // 更新评论
  const updateComment = (name: string, comment: string) => {
    setSelected(prev =>
      prev.map(sel => sel.name === name ? { ...sel, comment } : sel)
    );
  };

  // 更新排序
  const updateRank = (name: string, rank: number) => {
    setSelected(prev =>
      prev.map(sel => sel.name === name ? { ...sel, rank } : sel)
    );
  };

  // DI 可视化标记
  const applicantStats = useSelectionStats(applicants, selected);

  return (
    <div className="p-6 max-w-5xl mx-auto mt-6">
      <h2 className="text-2xl font-bold mb-4 text-center">Lecturer Review Panel</h2>

      {applicantStats.length === 0 ? (
        <p className="text-center text-gray-600">No applicant data found.</p>
      ) : (
        <ul className="space-y-4">
          {applicantStats.map((app, idx) => {
            const isSelected = selected.some(sel => sel.name === app.name);
            const selData = selected.find(sel => sel.name === app.name);

            return (
              <li key={idx} className={`
                border p-4 rounded shadow relative 
                ${app.isMostChosen ? "border-green-600 bg-green-50" : ""}
                ${app.isLeastChosen ? "border-yellow-500 bg-yellow-50" : ""}
                ${app.isUnchosen ? "border-gray-400 bg-gray-100" : ""}
              `}>
                <div className="flex justify-between items-center">
                  <p className="font-semibold text-lg">
                    {app.name} {isSelected && <span className="text-green-600">✅</span>}
                  </p>
                  {isSelected && (
                    <span className="text-sm font-bold text-white bg-blue-500 px-2 py-1 rounded">
                      Rank: {selData?.rank}
                    </span>
                  )}
                </div>

                <p className="text-sm"><strong>Course:</strong> {app.course}</p>
                <p className="text-sm">
                  <strong>Availability:</strong>{" "}
                  <span className="inline-block px-2 py-0.5 rounded text-white text-xs bg-yellow-500">
                    {app.availability}
                  </span>
                </p>

                <p className="text-sm">
                  <strong>Skills:</strong>{" "}
                  {app.skills.split(",").map((s, i) => (
                    <span key={i} className="inline-block bg-gray-200 text-gray-800 text-xs px-2 py-0.5 rounded mr-1">
                      {s.trim()}
                    </span>
                  ))}
                </p>

                <p className="text-sm"><strong>Credentials:</strong> {app.credentials}</p>

                {/* DI 标签显示 */}
                <div className="text-sm mt-2">
                  {app.isMostChosen && <span className="text-green-700 font-bold">🔥 Most Chosen</span>}
                  {app.isLeastChosen && <span className="text-yellow-700 font-bold">⚠️ Least Chosen</span>}
                  {app.isUnchosen && <span className="text-gray-700 italic">Not Selected</span>}
                </div>

                {/* 选择控件 */}
                <div className="mt-3">
                  <label className="mr-4">
                    <input
                      type="checkbox"
                      checked={isSelected}
                      onChange={() => toggleSelect(app.name)}
                    />
                    {" "}Select Candidate
                  </label>
                </div>

                {/* 选择后显示评语和排序 */}
                {isSelected && (
                  <div className="mt-3 space-y-2">
                    <div>
                      <label className="block font-semibold mb-1">Comment:</label>
                      <textarea
                        className="w-full border p-2 rounded"
                        value={selData?.comment || ""}
                        onChange={(e) => updateComment(app.name, e.target.value)}
                      />
                    </div>
                    <div>
                      <label className="block font-semibold mb-1">Rank (1-5):</label>
                      <input
                        type="number"
                        min={1}
                        max={5}
                        className="border p-2 rounded w-24"
                        value={selData?.rank || 1}
                        onChange={(e) => updateRank(app.name, parseInt(e.target.value))}
                      />
                    </div>
                  </div>
                )}
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
};

export default LecturerPanel;
