import React, { useEffect, useState } from "react";

export interface Applicant {
  name: string;
  course: string;
  availability: string;
  skills: string;
  credentials: string;
}

export interface Review {
  name: string;
  rank: number;
  comment: string;
  unselected: boolean;
}

export const useSelectionStats = (applicants: Applicant[], selected: Review[]) => {
  const minRank = Math.min(...selected.map((s) => s.rank));
  const maxRank = Math.max(...selected.map((s) => s.rank));

  return applicants.map((a) => {
    const sel = selected.find((s) => s.name === a.name);
    return {
      ...a,
      isMostChosen: sel?.rank === minRank,
      isLeastChosen: sel?.rank === maxRank,
      isNotChosen: sel?.unselected || !sel,
    };
  });
};

const LecturerPanel = () => {
  const [applicants, setApplicants] = useState<Applicant[]>([]);
  const [selected, setSelected] = useState<Review[]>([]);

  useEffect(() => {
    const baseApplicants: Applicant[] = [
      {
        name: "Leo Wang",
        course: "COSC2761",
        availability: "Full-time",
        skills: "Java, Spring Boot",
        credentials: "Bachelor of CS",
      },
      {
        name: "Jess Smith",
        course: "COSC2531",
        availability: "Casual",
        skills: "Python, Django",
        credentials: "PhD in AI",
      },
    ];

    try {
      const stored = localStorage.getItem("user");
      if (stored) {
        const data = JSON.parse(stored);
        baseApplicants.push({
          name: data.name?.trim() || "Test User",
          course: data.selectedCourses.join(", "),
          availability: data.availability,
          skills: data.skills.join(", "),
          credentials: data.credentials,
        });
      }
    } catch (err) {
      console.error("Invalid JSON in localStorage", err);
    }

    setApplicants(baseApplicants);
  }, []);

  const handleSelect = (name: string, checked: boolean) => {
    if (!checked) {
      setSelected((prev) => prev.map((s) => s.name === name ? { ...s, unselected: true } : s));
      return;
    }

    setSelected((prev) => [
      ...prev.filter((s) => s.name !== name),
      { name, rank: 1, comment: "", unselected: false },
    ]);
  };

  const handleCommentChange = (name: string, value: string) => {
    setSelected((prev) =>
      prev.map((s) => (s.name === name ? { ...s, comment: value } : s))
    );
  };

  const handleRankChange = (name: string, value: number) => {
    setSelected((prev) =>
      prev.map((s) => (s.name === name ? { ...s, rank: value } : s))
    );
  };

  const displayData = useSelectionStats(applicants, selected);

  return (
    <div className="p-6 max-w-5xl mx-auto mt-6">
      <h2 className="text-2xl font-bold mb-4 text-center">Lecturer Review Panel</h2>
      {displayData.length === 0 && <p className="text-center text-gray-500">No applicant data found.</p>}
      <ul className="space-y-4">
        {displayData.map((applicant, idx) => {
          const review = selected.find((s) => s.name === applicant.name);
          return (
            <li
              key={idx}
              className={`border p-4 rounded shadow relative ${
                applicant.isMostChosen ? "border-green-600 bg-green-50" :
                applicant.isLeastChosen ? "border-red-600 bg-red-50" :
                applicant.isNotChosen ? "border-gray-400 bg-gray-100" :
                "border-blue-400 bg-blue-50"
              }`}
            >
              <div className="flex justify-between items-center">
                <p className="font-semibold text-lg">
                  {applicant.name} {review && !review.unselected && <span className="text-green-600">âœ…</span>}
                </p>
                {review && !review.unselected && (
                  <span className="text-sm font-bold text-white bg-blue-500 px-2 py-1 rounded">
                    Rank: {review.rank}
                  </span>
                )}
              </div>

              <p className="text-sm">
                <strong>Course:</strong> {applicant.course}
              </p>
              <p className="text-sm">
                <strong>Availability:</strong> <span className="inline-block px-2 py-0.5 rounded text-white text-xs bg-yellow-500">{applicant.availability}</span>
              </p>
              <p className="text-sm">
                <strong>Skills:</strong> {applicant.skills.split(", ").map((skill, i) => (
                  <span key={i} className="inline-block bg-gray-200 text-gray-800 text-xs px-2 py-0.5 rounded mr-1">{skill}</span>
                ))}
              </p>
              <p className="text-sm">
                <strong>Credentials:</strong> {applicant.credentials}
              </p>

              <div className="text-sm mt-2">
                {applicant.isMostChosen && <span className="text-green-700 font-bold">ðŸ”¥ Most Chosen</span>}
                {applicant.isLeastChosen && <span className="text-red-700 font-bold ml-2">ðŸ’¤ Least Chosen</span>}
                {applicant.isNotChosen && <span className="text-gray-700 italic">Not Selected</span>}
              </div>

              <div className="mt-3">
                <label className="mr-4">
                  <input
                    type="checkbox"
                    aria-label="Select Candidate"
                    checked={!!review && !review.unselected}
                    onChange={(e) => handleSelect(applicant.name, e.target.checked)}
                  />
                  Select Candidate
                </label>
              </div>

              {review && !review.unselected && (
                <div className="mt-3 space-y-2">
                  <div>
                    <label htmlFor={`comment-${idx}`} className="block font-semibold mb-1">
                      Comment:
                    </label>
                    <textarea
                      id={`comment-${idx}`}
                      className="w-full border p-2 rounded"
                      value={review.comment}
                      onChange={(e) => handleCommentChange(applicant.name, e.target.value)}
                    />
                  </div>
                  <div>
                    <label htmlFor={`rank-${idx}`} className="block font-semibold mb-1">
                      Rank (1-5):
                    </label>
                    <input
                      id={`rank-${idx}`}
                      type="number"
                      className="border p-2 rounded w-24"
                      min={1}
                      max={5}
                      value={review.rank}
                      onChange={(e) => handleRankChange(applicant.name, parseInt(e.target.value))}
                    />
                  </div>
                </div>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default LecturerPanel;
