import { useState } from "react";
import { useNavigate } from "react-router-dom";
import ReCAPTCHA from "react-google-recaptcha";
import React from "react";

const LoginForm = () => {
  const [email, setEmail] = useState("");
  const [pwd, setPwd] = useState("");
  const [captcha, setCaptcha] = useState(false);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    if (!captcha) {
      setMessage("⚠️ 请完成 CAPTCHA 验证！");
      return;
    }

    const stored = JSON.parse(localStorage.getItem("user") || "{}");
    if (email === stored.email && pwd === stored.password) {
      setMessage("✅ 登录成功！");
      setTimeout(() => navigate("/lecturer"), 1000);
    } else {
      setMessage("❌ 登录失败，请检查邮箱或密码");
    }
  };

  return (
    <div className="p-6 max-w-md mx-auto mt-10 border rounded shadow">
      <h2 className="text-2xl font-bold mb-4 text-center">登录</h2>
      <input type="email" placeholder="邮箱" className="border p-2 mb-3 w-full rounded"
        onChange={(e) => setEmail(e.target.value)} />
      <input type="password" placeholder="密码" className="border p-2 mb-3 w-full rounded"
        onChange={(e) => setPwd(e.target.value)} />

      {/* CAPTCHA 验证组件 */}
      <div className="mb-3">
        <ReCAPTCHA
          sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
          onChange={() => setCaptcha(true)}
        />
      </div>

      <button className="w-full bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded shadow"
        onClick={handleLogin}>
        登录
      </button>
      {message && <p className="mt-3 text-center text-sm">{message}</p>}
    </div>
  );
};

export default LoginForm;
